var menuBgDefault = !1;
var them_obj={
	menu_dark:false, // منوی اصلی تاریک
	menu_color:'gradient-45deg-indigo-purple', //رنگ منوی اصلی در حالت انتخاب
	menu_select_rounded:'sidenav-active-square', //منوی اصلی در حالت انتخاب گردد شده یا معمولی
	collaps_menu:false, //منوی اصلی جمع شده
	//navbar_dark:false, //نوار بالا تاریک
	navbar_color:'gradient-45deg-indigo-purple', //رنگ نوار بالا
	navbar_fix:true, //ثابت کرد نوار بالا
	footer_dark:false, //فوتر تاریک
	footer_fix:false, //پاورقی ثابت
};

$(document).ready(function () {
    $(".theme-cutomizer").sidenav({ edge: "right" });
    new PerfectScrollbar(".theme-cutomizer", { suppressScrollX: !0 });
    function r(e) {
		e
            ? ($(".menu-dark-checkbox").prop("checked", !0), $(".sidenav-main").removeClass("sidenav-light").addClass("sidenav-dark"))
            : ($(".menu-dark-checkbox").prop("checked", !1), $(".sidenav-main").addClass("sidenav-light").removeClass("sidenav-dark"));
			them_obj.menu_dark = e;
    }
    function a(e) {
		e
            ? ($(".sidenav-main").removeClass("nav-lock"),
              $(".navbar-main.nav-collapsible").removeClass("sideNav-lock").addClass("nav-expanded"),
              $(".navbar-toggler i").html("radio_button_unchecked"),
              $("#main").addClass("main-full"),
              $(".sidenav-main.nav-collapsible, .navbar .brand-sidebar").trigger("mouseleave"),
			  $(".menu-collapsed-checkbox").prop("checked",!0))
            : ($(".sidenav-main").addClass("nav-lock").removeClass("nav-collapsed"),
              $(".navbar-main.nav-collapsible").addClass("sideNav-lock").removeClass("nav-collapsed"),
              $(".navbar-toggler i").html("radio_button_checked"),
              $("#main").removeClass("main-full"),
              $(".sidenav-main.nav-collapsible, .navbar .brand-sidebar").trigger("mouseenter"),
			  $(".menu-collapsed-checkbox").prop("checked",!1));
		them_obj.collaps_menu = e;
    }
    function o(e) {
		d(".navbar-main"),
            e
                ? ($(".navbar-dark-checkbox").prop("checked", !0), $(".navbar-main").removeClass("navbar-light").addClass("navbar-dark"))
                : ($(".navbar-dark-checkbox").prop("checked", !1), $(".navbar-main").removeClass("navbar-dark"));
		them_obj.navbar_dark = e;
		console.log(them_obj);
    }
    function n(e) {
		d(".page-footer");
		e
            ? ($(".footer-dark-checkbox").prop("checked", !0), $(".page-footer").removeClass("footer-light").addClass("footer-dark"))
            : ($(".footer-dark-checkbox").prop("checked", !1), $(".page-footer").addClass("footer-light").removeClass("footer-dark"));
		them_obj.footer_dark = e;
    }
    function d(e) {
        $(e).removeClass(
            "gradient-45deg-indigo-blue gradient-45deg-purple-deep-orange gradient-45deg-light-blue-cyan gradient-45deg-purple-amber gradient-45deg-purple-deep-purple gradient-45deg-deep-orange-orange gradient-45deg-green-teal gradient-45deg-indigo-light-blue gradient-45deg-red-pink red purple pink deep-purple cyan teal light-blue amber darken-3 brown darken-2 gradient-45deg-indigo-purple gradient-45deg-deep-purple-blue"
        );
    }
	function h_color_menu(color){
		var a,
			n,
			o = color
		menuBgDefault
			? (r(!0), (n = o), d(".sidenav-main"), $(".sidenav-main").addClass(n + " sidenav-gradient"))
			: ((a = o), d(".sidenav-main .sidenav li a.active"), $(".sidenav-main .sidenav li a.active").css({ background: "none", "box-shadow": "none" }), $(".sidenav-main .sidenav li a.active").addClass(a + " gradient-shadow"));
		them_obj.menu_color = color;
	}
	function h_menu_select_rounded(e){
		var a,n = e;
		(a = n), $(".sidenav-main").removeClass("sidenav-active-square sidenav-active-rounded").addClass(a);
		them_obj.menu_select_rounded=e;
	}
	function h_navbar_color(color){
		var a,
			n = color;
		(a = n), d(".navbar-main"), $(".navbar-main").addClass(a), $("body").hasClass("vertical-modern-menu") && (d(".content-wrapper-before"), $(".content-wrapper-before").addClass(a));
		them_obj.navbar_color=color
	}
	function h_navbar_fix(e){
		e
			? ($("#header .navbar").addClass("navbar-fixed"),$(".navbar-fixed-checkbox").prop("checked",!0))
			: ($("#header .navbar").removeClass("navbar-fixed"),$(".navbar-fixed-checkbox").prop("checked",!1));
		them_obj.navbar_fix = e;
	}
	function h_footer_fix(e){
		e
			? ($(".page-footer").addClass("footer-fixed").removeClass("footer-static"),$(".footer-fixed-checkbox").prop("checked",!0))
			: ($(".page-footer").removeClass("footer-fixed").addClass("footer-static"),$(".footer-fixed-checkbox").prop("checked",!1));
		them_obj.footer_fix = e;
	}

	/***********************************
		strat
	***********************************/
    $("body").hasClass("vertical-modern-menu") || $("body").hasClass("vertical-menu-nav-dark")
        ? $(".menu-bg-color").hide()
        : $("body").hasClass("vertical-gradient-menu") || $("body").hasClass("vertical-dark-menu")
        ? ($(".menu-color").hide(), (menuBgDefault = !0))
        : $("body").hasClass("horizontal-menu") && $(".menu-options").hide(),
        $(".menu-color-option, .menu-bg-color-option").click(function (e) {
			$(".menu-color .menu-color-option, .menu-bg-color .menu-bg-color-option").removeClass("selected"), $(this).addClass("selected");
			var color=$(this).attr("data-color");
			h_color_menu(color);
		}),
        $(".menu-dark-checkbox").click(function (e) {
			var e=$(".menu-dark-checkbox").prop("checked") ? !0 : !1;
			r(e);
		}),
        $(".menu-selection-radio").click(function (e) {
			var e=$(this).val();
			h_menu_select_rounded(e);
		}),
        $(".menu-collapsed-checkbox").click(function (e) {
			var e=$(".menu-collapsed-checkbox").prop("checked") ? !0 : !1;
			a(e);
		}),
        $(".navbar-color-option").click(function (e) {
			$(".navbar-color .navbar-color-option").removeClass("selected"), $(this).addClass("selected");
			var color=$(this).attr("data-color");
			h_navbar_color(color);
		}),
        $(".navbar-dark-checkbox").click(function (e) {
			var e=$(".navbar-dark-checkbox").prop("checked") ? !0 : !1;
			o(e);
		}),
        $(".navbar-fixed-checkbox").click(function (e) {
			var e=$(".navbar-fixed-checkbox").prop("checked");
			h_navbar_fix(e);
		}),
        $(".footer-dark-checkbox").click(function (e) {
			var e=$(".footer-dark-checkbox").prop("checked") ? !0 : !1;
			n(e)
		}),
        $(".footer-fixed-checkbox").click(function (e) {
			var e= $(".footer-fixed-checkbox").prop("checked");
			h_footer_fix(e)
		}),
        0 < $("html[data-textdirection='rtl']").length && $(".theme-cutomizer").sidenav({ edge: "left" });
	/***********************************
	 Run
	 ***********************************/
	function isObject(obj) {
		return (typeof obj === "object" && obj !== null) || typeof obj === "function";
	}
	var stKey=$appname+'_them_setting';
	function getLocalSetting(){
		let LS=window.localStorage;
		let str = LS.getItem(stKey);
		let obj = JSON.parse(str);
		if(isObject(obj)){
			return obj
		}
		return false;
	}
	function setLocalSetting(key,value){
		let LS=window.localStorage;
		let action = LS.setItem(key,JSON.stringify(value));
	}
	function action_them(obj){
		r(obj.menu_dark),
		a(obj.collaps_menu),
		h_navbar_color(obj.navbar_color),
		//o(obj.navbar_dark),
		n(obj.footer_dark),
		h_color_menu(obj.menu_color),
		h_menu_select_rounded(obj.menu_select_rounded),
		h_navbar_fix(obj.navbar_fix),
		h_footer_fix(obj.footer_fix);
	}
	save_them_seting = getLocalSetting()
	if(save_them_seting){
		action_them(save_them_seting);
	}else{
		$.get($baseurl+'/management/get-them-setting').done(function(res){
			if(res.result && res.data){
				let obj={};
				for(let k in res.data){
					if('true' === res.data[k]){
						obj[k] = true;
					}else if('false' === res.data[k]){
						obj[k] = false;
					}else{
						obj[k] = res.data[k];
					}
				}
				them_obj = obj;
				action_them(them_obj);
				if(!save_them_seting){
					setLocalSetting(stKey,them_obj);
				}
			}
		});
	}
	$("#save-setting-my-them").click(function(){
		$.post($baseurl+'/management/save-them-setting',
		{
			setting:them_obj,
			_token:$csrf_token
		}).done(function(r){
			if(r.result){
				setLocalSetting(stKey,them_obj);
				swal({
					title:"تنظیمات قالب ذخیره شد",
					icon:'success'
				})
			}
		})
	});
});
